INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '1.2.1', 8, 'Hi!', 'ہیلو!', 'Quick response to greeting', 'سلام کے فوری جواب', 1, '{"question": "Hi!", "question_urdu": "ہیلو!", "expected_answers": ["Hello!", "Hi!", "Hey!", "Good morning!", "Good afternoon!"], "expected_answers_urdu": ["ہیلو!", "ہائے!", "ہائے!", "صبح بخیر!", "دوپہر بخیر!"], "type": "question_answer"}', 1, 'greetings', 'beginner'),
('topic', '1.2.2', 8, 'How are you?', 'آپ کیسے ہیں؟', 'Response to how are you question', 'آپ کیسے ہیں کے سوال کا جواب', 2, '{"question": "How are you?", "question_urdu": "آپ کیسے ہیں؟", "expected_answers": ["I''m fine.", "I''m good.", "I''m well.", "Fine, thank you.", "Good, thanks."], "expected_answers_urdu": ["میں ٹھیک ہوں۔", "میں اچھا ہوں۔", "میں ٹھیک ہوں۔", "ٹھیک، شکریہ۔", "اچھا، شکریہ۔"], "type": "question_answer"}', 2, 'greetings', 'beginner'),
('topic', '1.2.3', 8, 'What''s your name?', 'آپ کا نام کیا ہے؟', 'Asking for name response', 'نام پوچھنے کا جواب', 3, '{"question": "What''s your name?", "question_urdu": "آپ کا نام کیا ہے؟", "expected_answers": ["My name is Ali.", "I''m Ali.", "Ali.", "My name''s Ali."], "expected_answers_urdu": ["میرا نام علی ہے۔", "میں علی ہوں۔", "علی۔", "میرا نام علی ہے۔"], "type": "question_answer"}', 3, 'introduction', 'beginner');

