-- Stage 2, Exercise 1: Daily Routine Narration (20 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '2.1.1', 10, 'What time do you wake up and what do you do first?', 'آپ کس وقت اٹھتے ہیں اور سب سے پہلے کیا کرتے ہیں؟', 'Morning routine narration', 'صبح کی روٹین کا بیان', 1, '{"phrase": "What time do you wake up and what do you do first?", "phrase_urdu": "آپ کس وقت اٹھتے ہیں اور سب سے پہلے کیا کرتے ہیں؟", "example": "I wake up at 6 a.m. and brush my teeth first.", "example_urdu": "میں صبح 6 بجے اٹھتا ہوں اور سب سے پہلے دانت صاف کرتا ہوں۔", "keywords": ["wake up", "brush teeth", "morning", "first"], "keywords_urdu": ["اٹھتے", "دانت صاف", "صبح", "پہلے"], "tense_focus": "present_simple", "sentence_structure": "time + action + sequence", "type": "daily_routine"}', 1, 'morning_routine', 'a2_beginner'),
('topic', '2.1.2', 10, 'What do you usually have for breakfast?', 'آپ عام طور پر ناشتے میں کیا کھاتے ہیں؟', 'Breakfast routine', 'ناشتے کی روٹین', 2, '{"phrase": "What do you usually have for breakfast?", "phrase_urdu": "آپ عام طور پر ناشتے میں کیا کھاتے ہیں؟", "example": "I usually eat eggs and toast for breakfast.", "example_urdu": "میں عام طور پر ناشتے میں انڈے اور ٹوسٹ کھاتا ہوں۔", "keywords": ["breakfast", "eggs", "toast", "eat", "usually"], "keywords_urdu": ["ناشتہ", "انڈے", "ٹوسٹ", "کھاتے", "عام طور پر"], "tense_focus": "present_simple", "sentence_structure": "frequency + food + meal", "type": "daily_routine"}', 2, 'morning_routine', 'a2_beginner');

-- Stage 2, Exercise 2: Question Answer Chat Practice (15 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '2.2.1', 11, 'Where do you live and what do you like about your neighborhood?', 'آپ کہاں رہتے ہیں اور آپ کو اپنے محلے کے بارے میں کیا پسند ہے؟', 'Location preferences chat', 'مقام کی ترجیحات کی گفتگو', 1, '{"question": "Where do you live and what do you like about your neighborhood?", "question_urdu": "آپ کہاں رہتے ہیں اور آپ کو اپنے محلے کے بارے میں کیا پسند ہے؟", "expected_answers": ["I live in Karachi and I like the friendly people in my neighborhood.", "I live in Lahore and I enjoy the parks and markets nearby.", "I live in Islamabad and I love the peaceful environment here."], "expected_answers_urdu": ["میں کراچی میں رہتا ہوں اور مجھے اپنے محلے کے دوستانہ لوگ پسند ہیں۔", "میں لاہور میں رہتا ہوں اور مجھے قریب کے پارکس اور مارکیٹس پسند ہیں۔", "میں اسلام آباد میں رہتا ہوں اور مجھے یہاں کا پرسکون ماحول پسند ہے۔"], "keywords": ["live", "Karachi", "Lahore", "Islamabad", "like", "neighborhood", "friendly", "parks", "peaceful"], "keywords_urdu": ["رہتے", "کراچی", "لاہور", "اسلام آباد", "پسند", "محلہ", "دوستانہ", "پارکس", "پرسکون"], "tense": "present_simple", "sentence_structure": "location + preference + reason", "type": "question_answer_chat"}', 1, 'location_preferences', 'a2_beginner');

-- Stage 2, Exercise 3: Roleplay Simulation (15 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '2.3.1', 12, 'Order Food at a Restaurant', 'ریستوران میں کھانا آرڈر کرنا', 'Restaurant ordering roleplay', 'ریستوران آرڈرنگ کا کردار', 1, '{"title": "Order Food at a Restaurant", "title_urdu": "ریستوران میں کھانا آرڈر کرنا", "description": "Practice ordering food at a restaurant with natural conversation flow.", "description_urdu": "قدرتی گفتگو کے ساتھ ریستوران میں کھانا آرڈر کرنے کی مشق کریں۔", "initial_prompt": "You are at a restaurant. The waiter asks: ''What would you like to eat?''", "initial_prompt_urdu": "آپ ریستوران میں ہیں۔ ویٹر پوچھتا ہے: ''آپ کیا کھانا چاہیں گے؟''", "scenario_context": "restaurant_ordering", "expected_keywords": ["like", "eat", "food", "menu", "order", "please"], "expected_keywords_urdu": ["پسند", "کھانا", "کھانے", "مینو", "آرڈر", "براہ کرم"], "ai_character": "waiter", "conversation_flow": "order_food", "cultural_context": "pakistani_restaurant", "type": "roleplay"}', 1, 'restaurant', 'a2_beginner');

