-- Stage 3, Exercise 1: Storytelling Narration (5 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '3.1.1', 13, 'Tell me about a time you felt really happy.', 'مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ واقعی خوش تھے۔', 'Narrative prompt about happiness', 'خوشی کے بارے میں بیانیہ اشارہ', 1, '{"prompt": "Tell me about a time you felt really happy.", "prompt_urdu": "مجھے ایک ایسے وقت کے بارے میں بتائیں جب آپ واقعی خوش تھے۔", "tense_focus": "past_simple", "expected_structure": "beginning_middle_end", "example_keywords": ["was", "were", "felt", "happy", "because", "when", "then", "finally"], "example_keywords_urdu": ["تھا", "تھے", "محسوس", "خوش", "کیونکہ", "جب", "پھر", "آخر میں"], "model_answer": "I was really happy when I got my first job. I had been looking for work for months, and when I finally got the call, I felt excited and relieved. Then I celebrated with my family, and finally I started working the next week.", "model_answer_urdu": "میں واقعی خوش تھا جب مجھے اپنی پہلی نوکری ملی۔ میں مہینوں سے کام تلاش کر رہا تھا، اور جب آخر میں مجھے کال آئی، تو میں پرجوش اور مطمئن محسوس کر رہا تھا۔ پھر میں نے اپنے خاندان کے ساتھ جشن منایا، اور آخر میں میں نے اگلے ہفتے کام شروع کر دیا۔", "evaluation_criteria": {"past_tense_usage": ["was", "were", "had", "got", "felt", "started"], "narrative_structure": ["when", "then", "finally", "because"], "descriptive_language": ["really", "excited", "relieved", "celebrated"], "fluency_indicators": ["smooth_transitions", "appropriate_pacing", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Develop narrative fluency in English", "Practice past tense verbs and time-based storytelling", "Gain confidence in speaking longer structured sentences", "Learn storytelling transitions: ''First,'', ''Then,'', ''Finally''"], "type": "narrative_prompt"}', 1, 'emotional_experiences', 'b1_intermediate');

-- Stage 3, Exercise 2: Group Discussion Simulation (15 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '3.2.1', 14, 'Planning a Weekend Trip', 'ہفتے کے آخر میں سفر کی منصوبہ بندی', 'Group discussion about trip planning', 'سفر کی منصوبہ بندی کے بارے میں گروپ بحث', 1, '{"title": "Planning a Weekend Trip", "title_urdu": "ہفتے کے آخر میں سفر کی منصوبہ بندی", "context": "You and your friends (AI) are planning a weekend trip.", "context_urdu": "آپ اور آپ کے دوست (AI) ہفتے کے آخر میں سفر کی منصوبہ بندی کر رہے ہیں۔", "conversation_flow": "decision_making", "initial_prompt": "Ali: Where should we go this weekend?", "initial_prompt_urdu": "علی: ہمیں اس ہفتے کے آخر میں کہاں جانا چاہیے؟", "follow_up_turns": [{"speaker": "Sara", "message": "I think the beach would be fun! We could swim and have a picnic.", "message_urdu": "میں سوچتی ہوں کہ ساحل سمندر تفریحی ہوگا! ہم تیر سکتے ہیں اور پکنک کر سکتے ہیں۔", "intent": "suggestion_with_reason"}, {"speaker": "Ali", "message": "What do you think, [your name]? Should we go to the beach or somewhere else?", "message_urdu": "آپ کیا سوچتے ہیں، [آپ کا نام]؟ کیا ہمیں ساحل سمندر جانا چاہیے یا کہیں اور؟", "intent": "asking_for_opinion"}], "expected_responses": [{"response": "I agree! The beach sounds perfect for a weekend trip.", "response_urdu": "میں متفق ہوں! ہفتے کے آخر میں سفر کے لیے ساحل سمندر بہترین لگتا ہے۔", "type": "agreement", "keywords": ["agree", "beach", "perfect", "weekend"]}], "evaluation_criteria": {"relevance_to_conversation": ["responds_to_question", "acknowledges_others", "contributes_meaningfully"], "appropriate_expressions": ["agreement_phrases", "disagreement_phrases", "opinion_phrases"], "fluency_and_tone": ["natural_flow", "polite_tone", "clear_pronunciation"], "minimum_score": 75}, "learning_objectives": ["Practice group discussion skills", "Learn to express opinions politely", "Develop conversation flow management", "Build confidence in group settings"], "type": "group_discussion"}', 1, 'travel_planning', 'b1_intermediate');

-- Stage 3, Exercise 3: Problem Solving Conversations (15 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '3.3.1', 15, 'Phone Not Working - Customer Support', 'فون کام نہیں کر رہا - کسٹمر سپورٹ', 'Customer support problem solving', 'کسٹمر سپورٹ مسئلہ حل کرنا', 1, '{"title": "Phone Not Working - Customer Support", "title_urdu": "فون کام نہیں کر رہا - کسٹمر سپورٹ", "problem_description": "Your phone is not working properly. You need to call customer support to get help.", "problem_description_urdu": "آپ کا فون درست طریقے سے کام نہیں کر رہا۔ آپ کو مدد کے لیے کسٹمر سپورٹ کو فون کرنا ہے۔", "scenario_type": "technical_issue", "context": "You bought a new phone last week, but it''s been having problems. The screen freezes, apps crash, and sometimes it doesn''t turn on. You need to call customer support to get it fixed or replaced.", "context_urdu": "آپ نے پچھلے ہفتے ایک نیا فون خریدا تھا، لیکن اس میں مسائل آ رہے ہیں۔ سکرین جم جاتی ہے، ایپس کریش ہو جاتی ہیں، اور کبھی کبھی یہ آن نہیں ہوتا۔ آپ کو اسے ٹھیک کرانے یا تبدیل کرانے کے لیے کسٹمر سپورٹ کو فون کرنا ہے۔", "expected_keywords": ["not working", "problem", "issue", "help", "please", "fix", "broken", "customer support", "technical", "assistance"], "expected_keywords_urdu": ["کام نہیں کر رہا", "مسئلہ", "مدد", "براہ کرم", "ٹھیک کریں", "خراب", "کسٹمر سپورٹ", "تکنیکی", "مدد"], "polite_phrases": ["Could you please help me with...", "I''m having trouble with...", "I would appreciate if you could...", "Would it be possible to...", "I''m sorry to bother you, but..."], "polite_phrases_urdu": ["کیا آپ براہ کرم میری مدد کر سکتے ہیں...", "مجھے مسئلہ آ رہا ہے...", "میں شکر گزار ہوں گا اگر آپ...", "کیا یہ ممکن ہے کہ...", "معذرت، لیکن..."], "sample_responses": ["Hi, I''m calling because my phone isn''t working properly. Could you please help me?", "I''m having trouble with my new phone. The screen keeps freezing and apps crash. Can you assist me?", "I bought this phone last week, but it''s not working correctly. Would it be possible to get it fixed or replaced?"], "evaluation_criteria": {"clarity": "Clear description of the problem and its symptoms", "politeness": "Use of polite phrases and respectful tone", "request_structure": "Proper way to ask for help or assistance", "specificity": "Providing specific details about the issue", "solution_oriented": "Asking for specific solutions or next steps"}, "learning_objectives": ["Learn polite problem-solving language", "Practice functional English for daily use", "Build confidence in requesting help professionally", "Use appropriate customer service vocabulary"], "type": "problem_solving"}', 1, 'customer_service', 'b1_intermediate');

