-- Stage 6, Exercise 1: Advanced Spontaneous Speaking (10 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '6.1.1', 22, 'The importance of boredom in fostering creativity and innovation', 'تخلیقی صلاحیت اور اختراع کو فروغ دینے میں بوریت کی اہمیت', 'Spontaneous speaking on abstract topic', 'تجرید موضوع پر خودکار بول چال', 1, '{"topic": "The importance of boredom in fostering creativity and innovation", "topic_type": "Abstract Reflection", "expected_structure": "Definition → Paradox → Benefits → Examples → Conclusion", "expected_keywords": ["creativity", "focus", "mindfulness", "productivity", "innovation", "reflection", "mental space", "inspiration", "contemplation", "breakthrough"], "vocabulary_focus": ["cognitive rest", "mental incubation", "creative breakthrough", "mindful awareness", "intellectual stimulation", "cognitive processing", "mental clarity", "innovative thinking", "contemplative practice", "creative potential"], "model_response": "Boredom, often dismissed as a negative state, actually serves as a crucial catalyst for creativity and innovation. When we''re bored, our minds are freed from the constant stimulation that characterizes modern life, allowing for deeper cognitive processing and creative incubation. This paradoxical relationship between boredom and creativity stems from the brain''s need for mental rest to process information and generate novel connections. During periods of boredom, our minds wander into uncharted territories, making unexpected associations that can lead to breakthrough ideas. History is replete with examples of great innovations emerging from moments of apparent inactivity—Einstein''s theory of relativity, Newton''s laws of motion, and countless artistic masterpieces. The key lies in embracing boredom as a form of cognitive rest rather than avoiding it through constant distraction. In our hyperconnected world, the ability to sit with boredom and allow the mind to wander freely has become a rare and valuable skill that directly correlates with creative output and innovative thinking.", "evaluation_criteria": {"spontaneous_fluency": 30, "depth_of_thought": 25, "advanced_vocabulary": 25, "structural_coherence": 20}, "type": "spontaneous_speaking"}', 1, 'Philosophical', 'C2');

-- Stage 6, Exercise 2: Advanced Diplomatic Communication (10 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '6.2.1', 23, 'Giving Critical Feedback', 'تنقیدی فیڈ بیک دینا', 'Diplomatic scenario for giving feedback', 'فیڈ بیک دینے کے لیے سفارتی منظر', 1, '{"scenario": "You''re a team leader giving critical feedback to a colleague who has been underperforming for several months, affecting team morale and project deadlines.", "scenario_type": "Performance Management", "context": "One-on-one meeting in private office", "stakeholder_emotions": "Defensive, anxious, potentially embarrassed", "expected_structure": "Opening → Specific Concerns → Impact → Support → Action Plan → Follow-up", "expected_keywords": ["feedback", "performance", "concerns", "support", "improvement", "expectations", "collaboration", "development", "timeline", "resources"], "vocabulary_focus": ["constructive feedback", "performance improvement", "professional development", "collaborative approach", "supportive environment", "clear expectations", "developmental goals", "performance metrics", "professional growth", "accountability"], "model_response": "I appreciate you taking the time to meet with me today. I want to have an open, constructive conversation about some concerns I''ve observed regarding your recent performance. Over the past few months, I''ve noticed that project deadlines have been consistently missed, and the quality of deliverables has fallen below our team''s standards. This has created challenges for the entire team and affected our ability to meet client expectations. I want to emphasize that this isn''t about placing blame, but rather about understanding what''s happening and how we can work together to address these issues. I''m committed to supporting your professional development and ensuring you have the resources and guidance you need to succeed. What factors do you think might be contributing to these challenges? I''d like to hear your perspective so we can develop a collaborative action plan that addresses both the immediate concerns and sets you up for long-term success.", "evaluation_criteria": {"tone_control": 30, "empathy_authority_balance": 25, "clarity_communication": 25, "conflict_resolution": 20}, "type": "diplomatic_scenario"}', 1, 'Professional Feedback', 'C2');

-- Stage 6, Exercise 3: Advanced Academic Debate (10 topics)
INSERT INTO public.ai_tutor_content_hierarchy (level, hierarchy_path, parent_id, title, title_urdu, description, description_urdu, topic_number, topic_data, topic_order, category, difficulty) VALUES
('topic', '6.3.1', 24, 'Should all education be free at all levels?', 'کیا تمام تعلیم تمام سطحوں پر مفت ہونی چاہیے؟', 'Advanced academic debate on education policy', 'تعلیمی پالیسی پر اعلیٰ تعلیمی بحث', 1, '{"topic": "Should all education be free at all levels?", "topic_urdu": "کیا تمام تعلیم تمام سطحوں پر مفت ہونی چاہیے؟", "topic_type": "Policy Debate", "controversy_level": "High", "expected_structure": "Thesis → Supporting Argument 1 → Supporting Argument 2 → Counterpoint → Conclusion", "expected_keywords": ["accessibility", "equity", "funding", "quality", "sustainability", "economic impact", "social mobility", "government responsibility", "private sector", "educational outcomes"], "expected_keywords_urdu": ["دستیابی", "انصاف", "فنڈنگ", "معیار", "پائیداری", "معاشی اثر", "سماجی نقل مکانی", "حکومتی ذمہ داری", "نجی شعبہ", "تعلیمی نتائج"], "vocabulary_focus": ["educational equity", "fiscal sustainability", "merit-based allocation", "socioeconomic barriers", "human capital investment", "public good", "market mechanisms", "redistributive policies", "academic excellence", "institutional autonomy"], "vocabulary_focus_urdu": ["تعلیمی انصاف", "مالی پائیداری", "قابلیت پر مبنی تقسیم", "سماجی اقتصادی رکاوٹیں", "انسانی سرمایہ کی سرمایہ کاری", "عوامی بھلائی", "بازاری میکانزم", "تقسیمی پالیسیاں", "تعلیمی فضیلت", "ادارہ جاتی خودمختاری"], "model_response": "The question of whether all education should be free at all levels touches upon fundamental principles of social justice, economic efficiency, and educational quality. Proponents argue that free education is essential for ensuring equal opportunity and social mobility, particularly for disadvantaged populations who face significant barriers to accessing quality education. From an economic perspective, education represents a public good that generates positive externalities for society as a whole, including reduced crime rates, increased civic engagement, and enhanced innovation capacity. However, critics raise valid concerns about fiscal sustainability and potential quality degradation. Implementing universal free education requires substantial government investment, which may strain public finances and necessitate difficult trade-offs with other essential services. Furthermore, there''s a risk that completely free education could lead to overcrowded institutions and reduced incentives for excellence. A nuanced approach might involve means-tested free education for primary and secondary levels, while maintaining some cost-sharing mechanisms for higher education to ensure both accessibility and quality maintenance.", "model_response_urdu": "یہ سوال کہ کیا تمام تعلیم تمام سطحوں پر مفت ہونی چاہیے، سماجی انصاف، معاشی کارکردگی، اور تعلیمی معیار کے بنیادی اصولوں کو چھوتا ہے۔ حامیوں کا کہنا ہے کہ مفت تعلیم مساوی مواقع اور سماجی نقل مکانی کو یقینی بنانے کے لیے ضروری ہے، خاص طور پر پسماندہ آبادی کے لیے جو معیاری تعلیم تک رسائی میں اہم رکاوٹوں کا سامنا کرتے ہیں۔ معاشی نقطہ نظر سے، تعلیم ایک عوامی بھلائی کی نمائندگی کرتی ہے جو پورے معاشرے کے لیے مثبت بیرونی اثرات پیدا کرتی ہے، جس میں جرم کی شرح میں کمی، شہری شرکت میں اضافہ، اور اختراعی صلاحیت میں بہتری شامل ہے۔ تاہم، نقاد مالی پائیداری اور ممکنہ معیار کی خرابی کے بارے میں درست خدشات اٹھاتے ہیں۔ عالمگیر مفت تعلیم کو نافذ کرنے کے لیے حکومتی سرمایہ کاری کی ضرورت ہے، جو عوامی مالیات پر دباؤ ڈال سکتی ہے اور دیگر ضروری خدمات کے ساتھ مشکل سمجھوتوں کو ضروری بنا سکتی ہے۔ مزید برآں، مکمل طور پر مفت تعلیم سے اداروں میں بھیڑ اور فضیلت کے لیے حوصلہ شکنی کا خطرہ ہے۔ ایک باریک بین نقطہ نظر میں پرائمری اور سیکنڈری سطحوں کے لیے وسائل پر مبنی مفت تعلیم شامل ہو سکتی ہے، جبکہ اعلیٰ تعلیم کے لیے دستیابی اور معیار کی بحالی دونوں کو یقینی بنانے کے لیے کچھ لاگت شیئرنگ کے میکانزم برقرار رکھے جا سکتے ہیں۔", "evaluation_criteria": {"argument_structure": 25, "evidence_usage": 25, "academic_tone": 20, "fluency_pacing": 15, "vocabulary_range": 15}, "learning_objectives": ["Formulate and defend policy arguments", "Use evidence and statistics in academic debates", "Master formal academic debate language", "Develop nuanced argumentation skills", "Build confidence in high-level academic discourse"], "type": "academic_debate"}', 1, 'Education Policy', 'C2');

-- =============================================================================
-- VERIFICATION QUERIES
-- =============================================================================

-- Verify comprehensive insertions
SELECT 'COMPREHENSIVE SAMPLE INSERTIONS SUMMARY:' as info;

-- Count topics by stage and exercise
SELECT 
    SUBSTRING(hierarchy_path FROM '^[0-9]+') as stage,
    SUBSTRING(hierarchy_path FROM '^[0-9]+\.[0-9]+') as exercise,
    COUNT(*) as topic_count
FROM public.ai_tutor_content_hierarchy 
WHERE level = 'topic'
GROUP BY SUBSTRING(hierarchy_path FROM '^[0-9]+'), SUBSTRING(hierarchy_path FROM '^[0-9]+\.[0-9]+')
ORDER BY stage::INTEGER, exercise;

-- Show sample data from each stage
SELECT 'SAMPLE DATA FROM EACH STAGE:' as info;
SELECT 
    hierarchy_path,
    title,
    topic_data->>'type' as data_type,
    category,
    difficulty
FROM public.ai_tutor_content_hierarchy 
WHERE level = 'topic'
ORDER BY hierarchy_path;

-- Summary of all exercises covered
SELECT 'ALL EXERCISES COVERED:' as info;
SELECT 
    CASE 
        WHEN hierarchy_path LIKE '1.1%' THEN 'Stage 1.1: Repeat After Me Phrases'
        WHEN hierarchy_path LIKE '1.2%' THEN 'Stage 1.2: Quick Response Prompts'
        WHEN hierarchy_path LIKE '1.3%' THEN 'Stage 1.3: Functional Dialogue'
        WHEN hierarchy_path LIKE '2.1%' THEN 'Stage 2.1: Daily Routine Narration'
        WHEN hierarchy_path LIKE '2.2%' THEN 'Stage 2.2: Question Answer Chat Practice'
        WHEN hierarchy_path LIKE '2.3%' THEN 'Stage 2.3: Roleplay Simulation'
        WHEN hierarchy_path LIKE '3.1%' THEN 'Stage 3.1: Storytelling Narration'
        WHEN hierarchy_path LIKE '3.2%' THEN 'Stage 3.2: Group Discussion Simulation'
        WHEN hierarchy_path LIKE '3.3%' THEN 'Stage 3.3: Problem Solving Conversations'
        WHEN hierarchy_path LIKE '4.1%' THEN 'Stage 4.1: Business Presentation Skills'
        WHEN hierarchy_path LIKE '4.2%' THEN 'Stage 4.2: Negotiation & Persuasion'
        WHEN hierarchy_path LIKE '4.3%' THEN 'Stage 4.3: Leadership Communication'
        WHEN hierarchy_path LIKE '5.1%' THEN 'Stage 5.1: Advanced Debate & Argumentation'
        WHEN hierarchy_path LIKE '5.2%' THEN 'Stage 5.2: Academic Presentation & Analysis'
        WHEN hierarchy_path LIKE '5.3%' THEN 'Stage 5.3: Professional Interview Mastery'
        WHEN hierarchy_path LIKE '6.1%' THEN 'Stage 6.1: Advanced Spontaneous Speaking'
        WHEN hierarchy_path LIKE '6.2%' THEN 'Stage 6.2: Advanced Diplomatic Communication'
        WHEN hierarchy_path LIKE '6.3%' THEN 'Stage 6.3: Advanced Academic Debate'
    END as exercise_name,
    COUNT(*) as sample_topics
FROM public.ai_tutor_content_hierarchy 
WHERE level = 'topic'
GROUP BY 
    CASE 
        WHEN hierarchy_path LIKE '1.1%' THEN 'Stage 1.1: Repeat After Me Phrases'
        WHEN hierarchy_path LIKE '1.2%' THEN 'Stage 1.2: Quick Response Prompts'
        WHEN hierarchy_path LIKE '1.3%' THEN 'Stage 1.3: Functional Dialogue'
        WHEN hierarchy_path LIKE '2.1%' THEN 'Stage 2.1: Daily Routine Narration'
        WHEN hierarchy_path LIKE '2.2%' THEN 'Stage 2.2: Question Answer Chat Practice'
        WHEN hierarchy_path LIKE '2.3%' THEN 'Stage 2.3: Roleplay Simulation'
        WHEN hierarchy_path LIKE '3.1%' THEN 'Stage 3.1: Storytelling Narration'
        WHEN hierarchy_path LIKE '3.2%' THEN 'Stage 3.2: Group Discussion Simulation'
        WHEN hierarchy_path LIKE '3.3%' THEN 'Stage 3.3: Problem Solving Conversations'
        WHEN hierarchy_path LIKE '4.1%' THEN 'Stage 4.1: Business Presentation Skills'
        WHEN hierarchy_path LIKE '4.2%' THEN 'Stage 4.2: Negotiation & Persuasion'
        WHEN hierarchy_path LIKE '4.3%' THEN 'Stage 4.3: Leadership Communication'
        WHEN hierarchy_path LIKE '5.1%' THEN 'Stage 5.1: Advanced Debate & Argumentation'
        WHEN hierarchy_path LIKE '5.2%' THEN 'Stage 5.2: Academic Presentation & Analysis'
        WHEN hierarchy_path LIKE '5.3%' THEN 'Stage 5.3: Professional Interview Mastery'
        WHEN hierarchy_path LIKE '6.1%' THEN 'Stage 6.1: Advanced Spontaneous Speaking'
        WHEN hierarchy_path LIKE '6.2%' THEN 'Stage 6.2: Advanced Diplomatic Communication'
        WHEN hierarchy_path LIKE '6.3%' THEN 'Stage 6.3: Advanced Academic Debate'
    END
ORDER BY exercise_name;
